import React from 'react';
import './Game.css';



class Square extends React.Component<any> {

  render() {
    return (
      <button className={this.props.className}></button>
    )
  }
}

class Board extends React.Component<{}, {isWhite:boolean}> {

  constructor(props:any) {
    super(props);
    this.state = {
      isWhite: true,
    };
  }

  renderSquare() {
    if (this.state.isWhite){
      this.setState({isWhite: !this.state.isWhite});
      return <Square className="white-square"/>;
    }
    else {
      this.setState({isWhite: !this.state.isWhite});
      return <Square className="black-square"/>;
    }
  }

  render() {
    var rows:any[] = [];
    for (let i=0; i<10; i++) {
