import React from 'react';
import './Game.css';
import Draggable from 'react-draggable';



class Square extends React.Component<any> {
  // constructor (props:any) {
  //   super(props);
  //   this.state = {
  //     hello: true
  //   };
  // }

  render() {
    return (
      // <button className={this.props.className}></button>
      <div className={this.props.className}>
        <Draggable>
          <div className="piece white" onClick={() => console.log("Hello")}>
          </div>
        </Draggable>
      </div>

    )
  }
}

class Board extends React.Component<{}> {


  renderSquare(color:string) {
    return <Square className={color}/>;
  }

  makeBoard() {
    var isWhite:boolean = true;
    var boardRows:any[] = new Array(8);
    for (let i=0; i<8; i++) {
      var row:any[] = new Array(8);
      for (let j=0; j<8; j++) {
        if (isWhite)
          row.push(this.renderSquare("square white"));
        else
          row.push(this.renderSquare("square black"));
        isWhite = !isWhite;
      }
      isWhite = !isWhite;
      boardRows.push(
        <div>
          {row}
        </div>
      )
    }

    return boardRows;

  }

  render() {
    return (
        <div className="board">
          {this.makeBoard()}
        </div>
    );
  }
}



class Game extends React.Component {

  render() {
    return (
      <div className="game">
        <Board/>
      </div>
    );
  }
}

export default Game;
